package textgen.la.models.ontology;

public class Adjective{
	String name, value;
	public Adjective (String name, String val) {
		this.name = name;
		this.value = val;
	}
	public String getName() {
		// TODO Auto-generated method stub
		return name;
	}
	public String getValue() {
		// TODO Auto-generated method stub
		return value;
	}
}
